static uint8_t data_master[kk * ii];
static uint8_t bb_master[(nn - kk) * ii];
static uint8_t tf_buffer[SDLP_TM_MAX_TF_LEN_RS];

void print_hex(const char *string)
{
    unsigned char *p = (unsigned char *) string;

    for (int i=0; i < SDLP_TM_MAX_TF_LEN_RS; ++i) {
        if (! (i % 16) && i)
            printf("\n");

        printf("%02x. ", p[i]);
    }
    printf("\n\n");
}

int main(int argc, char** argv) {
    FILE *fp;

    fp = fopen(argv[1], "r");
    uint8_t nextChar;
    uint16_t index = 0;
    while(index < SDLP_TM_MAX_TF_LEN_RS){
        if(feof(fp)){
            break;
        }
        tf_buffer[index] = fgetc(fp);
        index++;
    }
    fclose(fp);

    for (uint16_t i = 0; i < kk * ii; i++) {
        data_master[i] = tf_buffer[i];
    }
    //print_hex(data_master);
    int16_t i = 0;
    int16_t j = 0;
    int16_t feedback;
    uint8_t bb[nn - kk] = {0};

    for (uint8_t q = 0; q < ii; q++) {
        for (i = 0; i<nn-kk; i++) bb[i] = 0;    // Reset bb each iteration
        for (i = kk - 1; i >= 0; i--) {
            feedback = index_of[data_master[(kk * q) + i] ^ bb[nn - kk - 1]];
            if (feedback != -1) {
                for (j = nn - kk - 1; j > 0; j--)
                    if (gg[j] != -1)
                        bb[j] = bb[j - 1] ^ alpha_to[(gg[j] + feedback) % nn];
                    else {
                        bb[j] = bb[j - 1];
                    }
                bb[0] = alpha_to[(gg[0] + feedback) % nn];
            } else {
                for (j = nn - kk - 1; j > 0; j--) bb[j] = bb[j - 1];
                bb[0] = 0;
            };
        };
        for (uint8_t j = 0; j < nn - kk; j++) {
            bb_master[((nn - kk) * q) + j] = bb[j];
            printf("%02x.", bb[j]);
        }
        printf("\n\n");
    }
    /* Interleave Here */
    int16_t j_inter = 0; // only one j counter
    for (uint16_t q = 0; q < kk; q++) {
        for (uint16_t i = q; i < kk * ii; i += 223) {
            tf_buffer[j_inter] = data_master[i];
            j_inter++;
        }
    }
    for (uint8_t q = 0; q < (nn - kk); q++) {  // copy parity bits
        for (uint16_t i = q; i < (nn - kk) * ii; i += 32) {
            tf_buffer[j_inter] = bb_master[i];
            j_inter++;
        }
    }
    /* Randomize Here */

    fp = fopen("characters.bin", "w+");

    int16_t i_r = 0;
    for (i_r = 0; i_r < SDLP_TM_MAX_TF_LEN_RS; i_r++) {
        tf_buffer[i_r] ^= sequence[i_r];
        fputc(tf_buffer[i_r], fp);
    }
    fputc(EOF, fp);
    fclose(fp);


}